package com.example.drawme.ui.dashboard;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.lifecycle.Observer;
import androidx.lifecycle.ViewModelProviders;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.drawme.R;

import java.util.ArrayList;

public class DashboardFragment extends Fragment {

    private DashboardViewModel dashboardViewModel;
    private RecyclerView itemsRecView;

    public View onCreateView(@NonNull LayoutInflater inflater,
                             ViewGroup container, Bundle savedInstanceState) {
        dashboardViewModel =
                ViewModelProviders.of(this).get(DashboardViewModel.class);
        View root = inflater.inflate(R.layout.fragment_dashboard, container, false);
        ArrayList<Item> items = new ArrayList<>();
        items.add(new Item("khaxtran","data:image/png;base64,"));
        items.add(new Item("duc","data:image/png;base64,"));
        items.add(new Item("hoangdaniheo","data:image/png;base64,"));
        items.add(new Item("khoaml","data:image/png;base64,"));

        itemsRecView = root.findViewById(R.id.itemRecView);
        ItemAdapter adapter = new ItemAdapter(root.getContext());
        adapter.setItems(items);
        itemsRecView.setAdapter(adapter);
        itemsRecView.setLayoutManager(new LinearLayoutManager(root.getContext()));
        return root;
    }
}